import React, { useContext, useState, useRef } from "react";
import classes from "./Cart.module.css";
import Modal from "./../UI/Modal";
import CartContext from "../../context/cart-context";
import CartItem from "./CartItem";

const Cart = (props) => {
  // Overview of orders and option to proceed to step 2.
  const [ordering, setOrdering] = useState(true);
  // Step 2 - Order form with name, address and payment questions
  const [orderForm, setOrderForm] = useState(false);
  // Import Cart Context from the Context store.
  const cartContext = useContext(CartContext);
  // Show total amount fixed to 2 decimal places
  const totalAmount = `$${cartContext.totalAmount.toFixed(2)}`;
  // Check if the cart has items.
  const hasItems = cartContext.items.length > 0;
  // Standard payment options.
  const paymentOptions = ["Cash", "Gopay", "Paylater"];
  // Delivery times.
  const deliveryTimes = [
    "16:00",
    "16:15",
    "17:00",
    "17:15",
    "17:30",
    "17:45",
    "18:00",
    "18:15",
    "18:30",
    "18:45",
    "19:00",
    "19:15",
    "19:30",
    "19:45",
    "20:00",
  ];
  // Current date and time.
  const date = new Date();
  const currentTime =
    date.getHours() +
    ":" +
    (date.getMinutes() < 10 ? "0" : "") +
    date.getMinutes();
  // Checks currenttime and filter the delivery times shown.
  const availableDeliveryTimes = deliveryTimes.filter(
    (time) => time > currentTime
  );
  // Add +1 to the amount of the selected product in the cart.
  const cartItemAddHandler = (item) => {
    cartContext.addItem(item);
  };
  // Removes -1 to the amount of the selected product in the cart.
  const cartItemRemoveHandler = (id) => {
    cartContext.removeItem(id);
  };
  // Map throught the items in the cart and returns JSX with the styled CartItem component.
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartContext.items.map((item) => {
        console.log(item.price);
        return (
          <CartItem
            key={item.id}
            id={item.id}
            name={item.name}
            price={item.price}
            amount={item.amount}
            onAdd={cartItemAddHandler.bind(null, item)}
            onRemove={cartItemRemoveHandler.bind(null, item.id)}
          />
        );
      })}
    </ul>
  );
  // If user clicks next we go to the user information form, on orderform if user clicks cancel we go back to the
  const stepTwoHandler = () => {
    setOrdering((prev) => !prev);
    setOrderForm((prev) => !prev);
  };

  const checkboxChangeHandler = (e) => {
    const checkboxes = document.querySelectorAll("input[type=checkbox]");
    checkboxes.forEach((checkbox) => {
      if (checkbox !== e.target) {
        checkbox.checked = false;
      }
    });
  };

  const customerNameRef = useRef();
  const customerAddressRef = useRef();
  const deliveryTimeRef = useRef();
  const paymentMethodRefs = paymentOptions.map(() => React.createRef());

  const orderedItems = cartContext.items.map((item) => {
    return {
      name: item.name,
      price: item.price,
      amount: item.amount,
    };
  });

  const customerDetailSubmitHandler = (e) => {
    e.preventDefault();
    const customerName = customerNameRef.current.value;
    const customerAddress = customerAddressRef.current.value;
    const deliveryTime = deliveryTimeRef.current.value;
    if (!paymentMethodRefs) return;
    if (paymentMethodRefs) console.log(paymentMethodRefs);

    const paymentMethodIndex = paymentOptions.findIndex(
      (_, index) => paymentMethodRefs[index].current.checked
    );

    const selectedPaymentMethod =
      paymentMethodRefs[paymentMethodIndex].current.value;

    let orderInformation = {
      customerName,
      customerAddress,
      deliveryTime,
      paymentMethod: selectedPaymentMethod,
      orderedItems: orderedItems,
      totalAmount: totalAmount,
    };

    console.log(orderInformation);
  };

  return (
    <Modal setCartStatus={props.setCartStatus}>
      {ordering && (
        <div className={classes["cart-items"]}>
          {cartItems}
          <div className={classes.total}>
            <span>Total Amount</span>
            <span>{totalAmount}</span>
          </div>
          <div className={classes.actions}>
            <button
              className={classes["button--alt"]}
              onClick={props.setCartStatus}
            >
              Close
            </button>
            {hasItems && (
              <button className={classes.button} onClick={stepTwoHandler}>
                Order
              </button>
            )}
          </div>
        </div>
      )}
      {orderForm && (
        <form className={classes.form}>
          <label>Name</label>
          <input type="text" placeholder="name" ref={customerNameRef}></input>
          <label>Address</label>
          <input
            type="text"
            placeholder="Full address"
            ref={customerAddressRef}
          ></input>
          <label>Delivery time</label>
          <select name="deliveryTime" ref={deliveryTimeRef}>
            {availableDeliveryTimes.map((time) => (
              <option>{time}</option>
            ))}
          </select>
          <label>Payment method</label>
          <div className={classes.row}>
            {paymentOptions.map((option, index) => {
              return (
                <>
                  <input
                    key={option}
                    type="checkbox"
                    value={option}
                    ref={paymentMethodRefs[index]}
                    onChange={checkboxChangeHandler}
                  />
                  <label className={classes.label_payment}>{option}</label>
                </>
              );
            })}
          </div>
          <div className={classes.actions}>
            <button className={classes["button--alt"]} onClick={stepTwoHandler}>
              cancel
            </button>
            <button
              className={classes.button}
              onClick={customerDetailSubmitHandler}
            >
              next
            </button>
          </div>
        </form>
      )}
    </Modal>
  );
};

export default Cart;
