import { useContext, useState } from "react";
import classes from "./Cart.module.css";
import Modal from "./../UI/Modal";
import CartContext from "../../context/cart-context";
import CartItem from "./CartItem";

const Cart = (props) => {
  const [ordering, setOrdering] = useState(true);
  const [orderForm, setOrderForm] = useState(false);

  const cartContext = useContext(CartContext);
  const totalAmount = `$${cartContext.totalAmount.toFixed(2)}`;
  const hasItems = cartContext.items.length > 0;

  const cartItemAddHandler = (item) => {
    cartContext.addItem(item);
  };

  const cartItemRemoveHandler = (id) => {
    cartContext.removeItem(id);
  };

  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartContext.items.map((item) => {
        console.log(item);
        console.log(item.price);
        return (
          <CartItem
            key={item.id}
            id={item.id}
            name={item.name}
            price={item.price}
            amount={item.amount}
            onAdd={cartItemAddHandler.bind(null, item)}
            onRemove={cartItemRemoveHandler.bind(null, item.id)}
          />
        );
      })}
    </ul>
  );

  const stepTwoHandler = () => {
    setOrdering((prev) => !prev);
    setOrderForm((prev) => !prev);
  };

  const paymentOptions = ["Cash", "Gopay", "Paylater"];
  const deliveryTimes = [
    "16:00",
    "16:15",
    "17:00",
    "17:15",
    "17:30",
    "17:45",
    "18:00",
    "18:15",
    "18:30",
    "18:45",
    "19:00",
    "19:15",
    "19:30",
    "19:45",
    "20:00",
  ];

  const date = new Date();
  const currentTime =
    date.getHours() +
    ":" +
    (date.getMinutes() < 10 ? "0" : "") +
    date.getMinutes();
  console.log(currentTime);

  return (
    <Modal setCartStatus={props.setCartStatus}>
      {ordering && (
        <div className={classes["cart-items"]}>
          {cartItems}
          <div className={classes.total}>
            <span>Total Amount</span>
            <span>{totalAmount}</span>
          </div>
          <div className={classes.actions}>
            <button
              className={classes["button--alt"]}
              onClick={props.setCartStatus}
            >
              Close
            </button>
            {hasItems && (
              <button className={classes.button} onClick={stepTwoHandler}>
                Order
              </button>
            )}
          </div>
        </div>
      )}
      {orderForm && (
        <form className={classes.form}>
          <label>Name</label>
          <input type="text" placeholder="name"></input>
          <label>Address</label>
          <input type="text" placeholder="Full address"></input>
          <label>Delivery time</label>
          <select name="deliveryTime">
            {deliveryTimes.map((time) => (
              <option>{time}</option>
            ))}
          </select>
          <label>Payment method</label>
          <div className={classes.row}>
            {paymentOptions.map((option) => {
              return (
                <>
                  <input type="checkbox" value={option} />
                  <label className={classes.label_payment}>{option}</label>
                </>
              );
            })}
          </div>
          <div className={classes.row}>
            <button onClick={stepTwoHandler}>cancel</button>
            <button>next</button>
          </div>
        </form>
      )}
    </Modal>
  );
};

export default Cart;
