import React, { useContext, useEffect } from "react";
import classes from "./HeaderCartBtn.module.css";
import CartIcon from "../../components/Cart/CartIcon";
import CartContext from "../../context/cart-context";

const HeaderCartBtn = (props) => {
  const cartContext = useContext(CartContext);

  const numberOfCartItems = cartContext.items.reduce((current, item) => {
    return current + item.amount;
  }, 0);

  const btnAnimationClasses = `${classes.button} ${classes.bump}`;
  let appliedClass;

  useEffect(() => {
    if(numberOfCartItems > 0) {
      appliedClass = btnAnimationClasses;
    }else {
      appliedClass = `${classes.btn}`
    }

  },[numberOfCartItems]); 

  return (
    <button className={appliedClass} onClick={props.setCartStatus}>
      <span className={classes.icon}>
        <CartIcon />
      </span>
      <span>Your Cart</span>
        <span className={classes.badge}>{numberOfCartItems}</span>
    </button>
  );
};

export default HeaderCartBtn;
